css:
.item-container {
    width: 100%; /* Ensure the div takes full width */
    page-break-inside: avoid; /* Prevent page breaks inside the div */
    margin-bottom: 50px;
    position: relative;
    height: 100%;
}
.logo2 {
    position: absolute;
    right: -50px;
    width: 220px;
    height: 75px;
}

.item-images {
    display: flex;
    flex-direction: row;
    width: 100%;
    justify-content: center;
    margin-top: 40px;
}

.item-image1 img, .item-image2 img {
    max-width: 250px;
    min-width: 250px;
    max-height: 200px;
    min-height: 200px;
}

.content {
    margin-top: 30px;
    margin-bottom: 30px;
}

.item-image1 {
    margin-right: 50px;

}

.items-content {
    font-size: 20px !important;
    margin-top: 3px;
}

.big-content {
    display: flex;
}

.big-content .item-title {
    margin-right: 13px;
    word-wrap: break-word;
}
.header {
    border-bottom: 2px solid black;
    display: flex;
    flex-direction: column;
    height: 110px;
    justify-content: space-between;
    margin-bottom: 20px;
}

.header p {
    font-size: 14px;
}
.header-top, .header-bottom {
    display: flex;
    justify-content: space-between;
}

.project-header {
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
}

.project-logo {
    max-width: 150px;
    display: flex;
    justify-content: center;
    align-items: center;
    margin-right: 10px;
}

.project-logo img {
    max-width: 150px;
}


.logo img {
    width: 220px;
    height: 75px;
}

.footer {
    width: 100%;
}

.line-break {
    background: black;
    height: 3px;
    width: 90%;
}

.contact {
    display: flex;
    justify-content: space-between;
}
.page {
    border-bottom: 5px solid black;
    text-align: right;
    width: 10%;
}
.page p {
    margin-right: 4px;
    font-size: 16px;
}

.address {
    font-size: 13px;
    margin-top: 8px;
    margin-left: 6px;
    border-top: 2px solid black;
    width: 90%;
}

@media print {
    body {
        margin: 0;
        padding: 0;
    }
    .item-container:first-of-type {
        page-break-before: auto; /* ignore creating empty page on first div */
    }

    .item-container  {
        margin-bottom: 0px;
        width: 100%;
        page-break-inside: avoid;
        page-break-before: always; /* Force page breaks before the div */
        height: calc(100vh - 35px);
    }

    .content {
        margin-top: 50px;
    }

    .item-image1 img, .item-image2 img {
        max-width: 250px;
        min-width: 250px;
        max-height: 200px;
        min-height: 200px;
    }

    .footer {
        margin: 0;  
        position: absolute;
        bottom: 0px;
        width: 100%;
        page-break-after: always;
}

    .address {
        font-size: 16px;
    }

    @page {
        size: A4; /* Set paper size to A4 */
    }
}
